mapper.update:
update db_role
set update_time = now()
,role_name = #{roleName}
,remark = #{remark}

    ,delete_status = #{deleteStatus}

where id = #{id}

update db_role
set update_time = now()
  ,delete_status = #{deleteStatus}
where id = #{id}

mapper.select:
select id, role_name AS roleName, remark AS remark, create_time AS createTime, update_time AS updateTime from db_role
where role_name=#{roleName} and delete_status=0;
where id != #{roleId} and role_name=#{roleName} and delete_status=0;
