mapper.select:
select id, title, parent_id AS parentId, sorting, remark, create_time AS createTime, update_time AS updateTime from t_article_menu
where title=#{title} and parent_id=#{parentId} and delete_status=0;
where id!=#{articleMenuId} and parent_id=#{parentId} and title=#{title} and delete_status=0;

mapper.update:
update t_article_menu
set update_time = now()
,update_user_id = #{updateUserId}
,title = #{title}
,remark = #{remark}
,sorting = #{sorting}

    ,delete_status = #{deleteStatus}

where id = #{id}

update t_article_menu
set update_time    = now()
  , update_user_id = #{updateUserId}
  , delete_status  = #{deleteStatus}
where id = #{id}
